var cartLink=document.querySelectorAll(".catalog-items__buy");var cartBookmark=document.querySelectorAll(".catalog-items__bookmarks");var cartPopup=document.querySelector(".popup-cart");var cartClose=document.querySelectorAll(".modal__close");var cartContinue=document.querySelector(".modal-cart__button--continue");var bookmarkTab=document.querySelector(".header-top__item--bookmark");var cartTab=document.querySelector(".header-top__item--cart");console.log(cartContinue);for(var i=0;i<cartLink.length;i++){cartLink[i].addEventListener("click",function(a){a.preventDefault();cartPopup.classList.add("modal-show");cartTab.classList.add("header-top__item--active")})}for(var i=0;i<cartClose.length;i++){cartClose[i].addEventListener("click",function(a){a.preventDefault();cartPopup.classList.remove("modal-show")})}cartContinue.addEventListener("click",function(a){a.preventDefault();cartPopup.classList.remove("modal-show")});for(var i=0;i<cartBookmark.length;i++){cartBookmark[i].addEventListener("click",function(a){a.preventDefault();bookmarkTab.classList.add("header-top__item--active")})}window.addEventListener("keydown",function(a){if(a.keyCode===27){a.preventDefault();if(cartPopup.classList.contains("modal-show")){cartPopup.classList.remove("modal-show")}else{if(cartPopup.classList.contains("popup-error")){cartPopup.classList.remove("popup-error")}}}});