var cartLink=document.querySelectorAll(".catalog-items__buy");var cartBookmark=document.querySelectorAll(".catalog-items__bookmarks");var cartPopup=document.querySelector(".popup-cart");var cartClose=document.querySelectorAll(".modal__close");var bookmarkTab=document.querySelector(".header-top__item--bookmark");var cartTab=document.querySelector(".header-top__item--cart");for(var i=0;i<cartLink.length;i++){cartLink[i].addEventListener("click",function(a){a.preventDefault();cartPopup.classList.add("modal-show");cartTab.classList.add("header-top__item--active")})}for(var i=0;i<cartClose.length;i++){cartClose[i].addEventListener("click",function(a){a.preventDefault();cartPopup.classList.remove("modal-show")})}for(var i=0;i<cartBookmark.length;i++){cartBookmark[i].addEventListener("click",function(a){a.preventDefault();bookmarkTab.classList.add("header-top__item--active")})};